import Arrow from '../image/icon-arrow.png'
import ReactDOM from 'react-dom';
import '../style/globalInfo.css'


const GlobalInfo = ({ isOpenSide, onCloseSide }) => {


    return ReactDOM.createPortal(
      <div className=''>
        <div className={`sidenav ${isOpenSide ? 'open' : ''}`}>
        <button onClick={onCloseSide} className="close-btn">
          &times;
        </button>
        <ul>
          <li>Пункт меню 1</li>
          <li>Пункт меню 2</li>
          <li>Пункт меню 3</li>
          {/* Додайте інші пункти меню за потреби */}
        </ul>
      </div>
      </div>
      ,
      document.querySelector('.main-body')
    )
}

export default GlobalInfo;
